mapper.select:
SELECT  COUNT(*)
FROM    AUTH_USER
WHERE   REQUEST_ID=#{requestId}

mapper.insert:
INSERT
INTO    AUTH_USER(REQUEST_ID, AUTH_TYPE_ID, USER_ID, RESOURCE_ID, BEGIN_DATE, EXPIRE_DATE)
VALUES

    #{requestId}, #{authTypeId}, #{userId}, #{resourceId}, #{beginDate}, #{expireDate}

;

mapper.select:
SELECT  ID AS AUTH_USER_ID, RESOURCE_ID
FROM    AUTH_USER
WHERE   REQUEST_ID=#{requestId}
